import React,{useState, useEffect} from 'react';
import Axios from 'axios';
import './UserCmt.css'
function UserCmt (){
    // var anh='http://localhost:4000/anh/thun1_3' ;
    const [list,setlist]=useState([])
    const [sentence,setsentence]=useState('')
    const [name,setname]=('hoanganh')
    useEffect(()=>{
        var {match}=this.props;
        con
        Axios.get("chat").then(res=>{
            setlist(res.data)
        })
        Axios.post("/getproduct",{link:match.params.name}).then(
            res=>{
                console.log(res.data[0])
                if (res.data[0]!==undefined){
                    this.setState({
                        data:res.data[0],sl:res.data[0].sizeS  ,loading:1  
                    });
                }
                
                // console.log(this.state.data)
            }
        ) 
    },[list])

    function changeSentence(e){
        setsentence(e.target.value)
        }
    function send(){
        Axios.post('cmt',{name:name,sentence:sentence})
    }
    return(
        <div >
            <div className="framchat container">
                {list.map(value=>{
                    return(
                        <div>
                            <h5>{value.sentence}</h5>
                            <p>{value.name}</p>
                        </div>
                    )
                })}
                <div className="row" >
                    <div className="col-8" type="text">
                    <textarea type="text" className="tex" name="sentence" value={sentence} onChange={changeSentence}></textarea>
                    </div>
                    <div className="col-1">
                        <button onClick={send}>send </button>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default UserCmt;
