import React,{Component} from 'react';
import Frame from '../Frame/Frame';
import './Shirt.css'

import axios from "axios"
class Shirt extends Component{
    constructor(props) {
        super(props);
        this.state = {
            currentPage:1,
            newsPerPage:6,
            pageNumbers:1
        };
      }
    changepage=(pageNumbers)=>{
        this.setState({
            pageNumbers:pageNumbers
        })
    }

    Show = (menus,match,currentPage) => {
        var result = null;
        if (menus !== undefined){
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                var link=menu.Link+"_1"
                if (index>=(currentPage-1)*this.state.newsPerPage && index <= currentPage*this.state.newsPerPage-1)
                return(
                    <Frame match={match.match} className="flex-left" anh={link} ten={menu.Name} giamoi={menu.NewPrice} giacu={menu.Price} to={menu.Link}>  </Frame>
                )
            })
        }}
        return result;
    }
    render(){
        var match=this.props;
        var abc=match.location.hash;
        var abc1,length;
        if (this.props.data !== undefined) length=this.props.data.length;
        if (length%6==0) length=length/6; else length=parseInt(length/6)+1;
        console.log(length)

        if (abc === "") abc1=1; else  abc1=abc.slice(1)
        // console.log(abc1)
        
        if (this.state.currentPage!==abc1)
        this.setState({
            currentPage:abc1
        })
        if (length !== NaN){
            if (this.state.pageNumbers!==length)
                this.setState({
                pageNumbers:length
        })
        }
        
        return(
            <div className="shirt">
                <pre className="color-darkgray">HOME  <i class="fas fa-arrow-right color-black"></i> <b className="color-black"> {this.props.Name}</b></pre>
                <div className="page row">
                    <span className="col-3" >Trang {this.state.currentPage} trên tổng số 5 :</span>
                    <ul className="col ">
                    <li className="pagelink "><a href="#1" > 1</a></li>
                    <li className="pagelink "><a href="#2" > 2</a></li>
                    </ul>
                </div>
                <div className=" flex-container ">
                    {/* <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" to="aaa"  ></Frame> */}
                    {this.Show(this.props.data,match,this.state.currentPage)}
                </div>
                
                <div className="page row">
                    <span className="col-3" >Trang {this.state.currentPage} trên tổng số 5 :</span>
                    <ul className="col ">
                    <li className="pagelink "><a href="#1" > 1</a></li>
                    <li className="pagelink "><a href="#2" > 2</a></li>
                    </ul>
                </div>
            </div>
        
        )
}}
export default Shirt
