import React,{Component} from 'react';
import './Products.css';
import axios from "axios"
// import './Shirt.css'
class Products extends Component{
    constructor(props) {
        super(props);
     
        this.state = {
            new: "",
            soluong:1,
            id:1,
            class1:["col a",'col a1','col a1'],
            size:"S",
            name:"Áo sơ mi dài tay phối bèo cổ",
            giagiam:222.600,
            gia:328.200,
            mota:"Áo sơ mi dài tay phối bèo cổ Chất liệu vải thô mềm"
        };
      }
      up = () => {
            var d=this.state.soluong + 1;
            this.setState({
                soluong:d
            });
    }
        down = () => {
            var d=this.state.soluong;
            if (d>1) d=d-1;
            this.setState({
                soluong:d
            });
    }
    clicksize = (dem) => {
        var newArray=["col a1",'col a1','col a1'];
        var newArray1=["S","M","L",""];
        var abc=newArray1[dem];
        newArray[dem]="col a";
        this.setState({
            class1:newArray,
            size:abc
        });
    }
    add=()=>{
        var id=this.state.id;
        var soluong=this.state.soluong;
        var size=this.state.size;
        var name=this.state.name;
        var gia=this.state.giagiam;
        axios.post('/add',{id:id,soluong:soluong,size:size,name:name,gia:gia})
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
       }
    render(){
        var {match}=this.props;
        console.log(match.params.name);
        var link='http://localhost:4000/anh/'+match.params.name;
        console.log(link+'_nho5');
        return(
            <div className="product">
                <pre className="color-darkgray">HOME  <i className="fas fa-arrow-right color-black"></i> <b className="color-black"> {this.props.ten}</b></pre>
                <div className="boder-product">
                    <div className=" row ">
                        <div className=" col-6 ">
                            <a href={link+'_1'} className="MagicZoom abc24" id="trainer"  data-options="zoomPosition: inner"><img src={link+'_to'}></img></a>
                            <div className="abc2" >
                                <a data-zoom-id="trainer" href={link+'_to'} data-image={link+'_to'}><img src={link+'_nho1'}></img></a>
                                <a data-zoom-id="trainer" href={link+'_to'} data-image={link+'_to'}><img src={link+'_nho2'}></img></a>
                                <a data-zoom-id="trainer" href={link+'_to'} data-image={link+'_to'}><img src={link+'_nho3'}></img></a>
                                <a data-zoom-id="trainer" href={link+'_to'} data-image="http://localhost:4000/anh/12be"><img src={link+'_nho4'}></img></a>
                                <a data-zoom-id="trainer" href={link+'_to'} data-image="http://localhost:4000/anh/12be"><img src={link+'_nho5'}></img></a>
                            </div>
                        </div>
                        <div className="col"></div>
                        <div className="col-5">
                            <p className="name-product">{this.state.name}</p>
                            <p className="gia">
                                <span className="giacu"> {this.state.gia +'đ'}</span>
                                <span className="giamoi"> {this.state.giagiam +"đ"}</span>
                            </p>
                            <div className="sizeM">
                                <p>SIZE <b className="color-red">{this.state.size} *</b></p>
                                <div className="row" >
                                    <div className='col row'>
                                        <div className={this.state.class1[0]} onClick={() =>  this.clicksize(0)} >
                                            <button className="size tooltip1">S<span className="tooltiptext1">S</span></button>
                                        </div>
                                        <div className={this.state.class1[1]} onClick={() =>  this.clicksize(1)}>
                                            <button className="size tooltip1">M<span className="tooltiptext1">M</span></button>
                                        </div>
                                        <div className={this.state.class1[2]} onClick={() =>  this.clicksize(2)}>
                                            <button className="size tooltip1">L<span className="tooltiptext1">L</span></button>
                                        </div>
                                    </div>
                                    <div className='col'></div>
                                    <div className='col'></div>
                                </div>
                            </div>
                            <p className="color-red">* Thông tin bắt buộc</p>
                            <div className="row">
                                <div className="col-5 row">
                                    <button className="col-3 a12 tooltip1" onClick={this.down}>
                                        <b>-</b>
                                        <span className="tooltiptext1">decrease</span>   
                                    </button>
                                    <div className="col-6 a12">{this.state.soluong}</div>
                                    <button className="col-3 a12 tooltip1" onClick={this.up}>
                                        <b>+</b>
                                        <span className="tooltiptext1">increase</span>
                                    </button>
                                </div>
                                <div className="col-7"></div>
                            </div>
                            <a href=""><button className="add" onClick={this.add}>THÊM VÀO GIỎ HÀNG</button></a>
                            <p>MÔ TẢ SẢN PHẨM</p>
                            <p>{this.state.mota}</p>
                        </div>
                    </div>
                </div>
                <div className="product-buttom">
                    <div className="aaa">
                        <div className="aaa1">
                            <p>THÔNG TIN SẢN PHẨM</p>
                        </div>
                    </div>
                    <div className="aaa2"><p>{this.state.mota}</p></div>
                </div>
                <div className="product-buttom">
                    <div className="aaa">
                        <div className="aaa1">
                            <p>ĐÁNH GIÁ SẢN PHẨM</p>
                        </div>
                    </div>
                    <div className="user">
                    <i class="far fa-user"></i>
                    </div>
                </div>
            </div>
        )
}}
export default Products
