import React,{Component} from 'react';
// import './Admin.css'
import Highcharts from 'highcharts';
import axios from 'axios';
import Linecharts from './Linecharts';
import HighchartsReact from 'highcharts-react-official';
class HomeAdmin extends Component{
  constructor(props) {
    super(props); 
    var today=new Date();
    this.state = {
      d:1,
      chartOptions:{
        chart: {
            type: 'line'
        },
        title: {
            text: 'Doanh thu theo tháng'
        },
        subtitle: {
            text: ''
        },
        xAxis: {
             categories: ['ngày 1--> ngày 5','ngày 6--> ngày 10','ngày 11--> ngày 15','ngày 16--> ngày 20','ngày 21--> ngày 25','ngày 26 --> cuối tháng']
        },
        yAxis: {
            title: {
                text: 'Doanh Thu'
            }
        }, 
        legend: {
            layout: 'vertical',
            align: 'right',
            verticalAlign: 'middle'
        },
        plotOptions: {
            series: {
                label: {
                    connectorAllowed: false
                },
            }
        },
        series: [ {
            name: 'London',
            data: [1500000,1000000,2000000,2500000,4000000,]
          },{
              name:'thang 1',
              data:[3522000,2258000,3000000]
          }],
          responsive: {
            rules: [{
                condition: {
                    maxWidth: 500
                },
                chartOptions: {
                    legend: {
                        layout: 'horizontal',
                        align: 'center',
                        verticalAlign: 'bottom'
                    }
                }
            }]
        }
    }
    }
    
  }
  componentDidMount() { 
      
      axios.get('/revenue12')
      .then(res=>{
        var series=this.state.chartOptions.series;
        var d=this.state.d;
        var today=new Date;
        series[d+1]={
          name: 'Tháng '+ (today.getMonth()+1),
          data: res.data
        }
        this.setState({
            chartOptions: {
                series: series
              },
            d:d+1
            
      });
    })
  }
  
   a=()=>{
    var dem=this.state.dem;
    dem[this.state.d+1]={a:'b'}
    this.setState({
      dem:dem
    })
    
    console.log(this.state.dem)
   }
    render(){
        return(
        <div className="navright">
            <h1 >Admin</h1>
            {/* <Linecharts data={this.state.series}></Linecharts> */}
            <HighchartsReact
                highcharts={Highcharts}
                options={this.state.chartOptions}
            />
            <button onClick={this.a} >aaaaa</button>
        </div>
      )
    }
}
export default HomeAdmin;
