import React,{Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css'
import axios from "axios"
import {Redirect} from 'react-router-dom'
class Login extends Component {
  constructor (props) {
    super(props)
    this.state = {
      kq:"that bai"
    }
 }
 componentDidMount() {
  
  axios.get('/test')
  .then(res=>{
    this.setState({
      name:res.data
  });
  })
}
  handleRequert(e){
    e.preventDefault();
    var {username,password }=this.refs;
   axios.post('/login1',{username:username.value,password:password.value})
    .then(res=>{
       if (res.data="that bai") {this.setState({kq:"that bai"})} else  this.setState({a:"thanh cong"}) ;
    //  console.log(res.data);
     console.log(res.data);
   })
    // .catch(err => console.log(err))
  }
  render(){
    if (this.state.kq=="thanh cong") {
      return <Redirect to="/"/>
    }
  return (
    <div className="login1">
      <div class="page-title">
        <h1 className="size32">Khách hàng đã đăng ký</h1>
        {/* <div class="field note">If you have an account, sign in with your email address.</div> */}
      </div>
      <div className="li">
          <span>Login and password are required.</span>
      </div>
     
      <form onSubmit={this.handleRequert.bind(this) }  className="lo"> 
        <div ><input placeholder="Username"  type="text" id="username" ref="username" className="borderinput" ></input></div>
        <div><input placeholder="Password"  type="password" id="password" ref="password" className="borderinput"></input></div>
        <a ><button type="submit" onClick={this.handleRequert.bind(this) } ><b>ĐĂNG NHẬP</b></button></a>
      </form>
    </div>
  );
}}

export default Login;
