import React,{Component} from 'react';
import './Admin.css'
import axios from "axios"
import {
    BrowserRouter as Router,
    Route, NavLink, Switch
} from "react-router-dom";
class Admin extends Component{
    constructor(props) {
        super(props);
        this.state = {
            Name:"",
            Price:0,
            NewPrice:0,
            Link:"",
            Description:"",
            name_kodau:""
        };
      }
      add=()=>{
        var Name=this.state.Name;
        var Price=this.state.Price;
        var NewPrice=this.state.NewPrice;
        var Link=this.state.Link;
        var Description=this.state.Description;
        var name_kodau=this.state.name_kodau;
        axios.post('/addproduct',{Name:Name,Price:Price,NewPrice:NewPrice,Link:Link,Description:Description,name_kodau:name_kodau})
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
       } 
       onChange =(e)=>{
        // console.log(e.target)
        var name=e.target.name;
        var value=e.target.value;
        this.setState({
          [name]:value
        })
      }
    render(){
      return(
        <Router>
            <div className="nav1">
                <ul>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                </ul>
                
            </div>
            <div className="navright">
                <div> 
                    <p> tên sản phẩm</p>
                    <input value={this.state.Name} onChange></input>
                </div>
                <div>
                    <p> giá sản phẩm</p>
                    <input type="number" min="0" value={this.state.Price}></input>
                </div>
                <div>
                    <p> giá mới</p>
                    <input type="number" min="0" value={this.state.NewPrice}></input>
                </div>
                <div>
                    <p> link </p>
                    <input value={this.state.Link}></input>
                </div>
                <div>
                    <p> mô tả sản phẩm </p>
                    <input value={this.state.Description}></input>
                </div>
                <div>
                    <p> tên sản phẩm không dấu </p>
                    <input value={this.state.name_kodau}></input>
                </div>
                <button>ADD</button>
            </div>
        </Router>
      )
    }
}
export default Admin;
