import React,{Component} from 'react';
import './Order.css'
import dayjs from "dayjs"
import axios from "axios";
class Orders extends Component{
    constructor(props) {
        super(props);
        this.state = {
            date:dayjs().format("YYYY-MM-DD"),
            data: [],
            data1: [],
            name: "",
            tong: []
        };
      }
      componentDidMount() {
        axios.get("/history").then(res => {
          this.setState({
            data: res.data
          });
        });
        axios.get("/history_time").then(res => {
          this.setState({
            data1: res.data
          });
        });
        axios.get("/test").then(res => {
            console.log(res.data);
          this.setState({
            name: res.data
          });
        });
      }
      Show1 = (menus,name,i) => {
        var result = null;
        var tong=0;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                if (menu.time==name ){
                    tong=tong+menu.NewPrice*menu.soluong;
                    // console.log(index);
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><a href={menu.Link}><img src={"http://localhost:4000/anh/"+menu.Link+'_nho1'}></img></a></div>
                                <div className="col-9">
                                    <a href={menu.Link}><h5>{menu.Name}</h5></a>
                                    <h6>Size:  {menu.size}</h6>
                                </div>
                            </div>
                            <div className="col-5 row t1t1">
                                <div className="col-4"><p>{menu.NewPrice}</p></div>
                                <div className="col-4">
                                    <p>{menu.soluong} </p>
                                </div>
                                <div className="col-4"><p>{menu.NewPrice*menu.soluong}</p></div>
                            </div>
                        </div>
                    )
                }
                // i=i+1;
            })
            if (tong!=this.state.tong[i]){
                var t=this.state.tong;
                t[i]=tong;
                this.setState({
                    tong:t
                });
            }
            
        }
        return result;
    }
      Show = (data, times) => {
        var result = null;
        console.log(data, times);
        console.log(times.length);
        // var tong=0;
        if (times.length > 0) {
          // for (var i=0;i<=times.length-1;i++){
          result = times.map((time, index) => {
              console.log(data)
            return (
              <div className="tb">
                <div>NGÀY VÀ GIỜ: {time.time} </div>
                <div className="tb1">
                  <div className="row tb1top">
                    <div className="col-7 clear">
                      <p className="">TÊN SẢN PHẨM</p>
                    </div>
                    <div className="col-5 row">
                      <div className="col-4">
                        <p>GIÁ TIỀN</p>
                      </div>
                      <div className="col-4">
                        <p>SỐ LƯỢNG</p>
                      </div>
                      <div className="col-4">
                        <p>CỘNG</p>
                      </div>
                    </div>
                  </div>
                  {this.Show1(data,time.time,index)}
                </div>
                <div className="t_right">TỔNG TIỀN : {this.state.tong[index]} </div>
              </div>
            );
          });
        }
        return result;
    
      };
    
    //   const today=dayjs();
    onChange =(e)=>{
        console.log(e.target)
        var name=e.target.name;
        var value=e.target.value;
        this.setState({
          [name]:value
        })
        console.log(e.target.value)
        var value1=value[8]+value[9]
        // value1=value.add('1','day')

        console.log(va)
        axios.post('/orderadmin',{date:e.target.value})
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
      }
      render(){
       console.log(this.state.date) 
    //    var date=this.state.date;
    //    var date1=date.format("YYYY-MM-DD");
    //    console.log(date)
      return(
        <div  className="navright">
            <h1> Đơn Hàng</h1>
            <div className="order">
                <input type="date" name="date" value={this.state.date} onChange={this.onChange} ></input>
                {this.Show(this.state.data, this.state.data1)}
            </div>
            <div>

            </div>
        </div>
      )
    }
}
export default Orders;
