import React,{Component} from 'react';
import './Main.css';
import Header from './Header/Header';
import Body from './Body/Body';
import Footer from './Footer/Footer';

import Out from './Out/Out';
class Main extends React.Component{
  componentDidMount() {
    window.addEventListener("scroll", this.handleScroll);
  }
  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }
  handleScroll = () => {
    const { prevScrollpos } = this.state;

    const currentScrollPos = window.pageYOffset;
    const visible = prevScrollpos > currentScrollPos;
    this.setState({
      prevScrollpos: currentScrollPos
    });
    // console.log(this.state.prevScrollpos)
    if (this.state.prevScrollpos>200){
      this.setState({
        class:"navbar1 navbar2"
      })
    } else {
      this.setState({
        class:"navbar1"
      })
    }
  };
  scrollToTop() {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  }
  render(){
      return(
        <div>
            <Header></Header>
            <Body></Body>
            <Footer></Footer>
            <a  className="return-to-top" onClick={this.scrollToTop} data-toggle="tooltip"><i class="icon-chevron-up"></i></a>
            <a  className="return-to-bottom" data-toggle="tooltip"><i class="icon-chevron-down"></i></a>
        </div>
      )
  }
}
export default Main;
