'use strict';
/**
 * Sequelize module for debug and deprecation messages.
 * It require a `context` for which messages will be printed.
 *
 * @module logging
 * @private
 */

const debug = require('debug');

const util = require('util');

class Logger {
  constructor(config) {
    this.config = Object.assign({
      context: 'sequelize',
      debug: true
    }, config);
  }

  warn(message) {
    // eslint-disable-next-line no-console
    console.warn("(".concat(this.config.context, ") Warning: ").concat(message));
  }

  inspect(value) {
    return util.inspect(value, false, 3);
  }

  debugContext(name) {
    return debug("".concat(this.config.context, ":").concat(name));
  }

}

exports.logger = new Logger();
exports.Logger = Logger;
