import React,{Component} from 'react';
import Frame from '../Frame/Frame';
import './Shirt.css'
import { Form } from 'react-bootstrap';
import axios from "axios"
class Shirt extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };
      }
      componentDidMount() {
        axios.get('/shirt')
        .then(res=>{
          console.log(res.data);
          this.setState({
            data:res.data
        });
        })
    }
    Show = (menus,name) => {
        var result = null;
        var tong=0;
        var i=0;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                if (menu.username==name){
                    tong=tong+menu.gia*menu.soluong;
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><img src="http://localhost:4000/anh/12tiny"></img></div>
                                <div className="col-9">
                                    <h5>{menu.name}</h5>
                                    <h6>Size:  {menu.size}</h6>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>{menu.gia}</p></div>
                                <div className="col">
                                    <input type="number" value={menu.soluong} min="1"  onChange={this.change}></input>
                                </div>
                                <div className="col"><p>{menu.gia*menu.soluong}</p></div>
                            </div>
                        </div>
                    )
                }
                i=i+1;
            })
            if (tong!=this.state.tong)
            this.setState({
                tong:tong
            });
        }
        return result;
    }
    render(){
        return(
            <div className="shirt">
                <pre className="color-darkgray">HOME  <i class="fas fa-arrow-right color-black"></i> <b className="color-black"> SHIRT</b></pre>
                <p class="size-20"><b>ÁO</b></p>
                <div className=" flex-container ">
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000"  ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" ></Frame>
                </div>
                
            </div>
        
        )
}}
export default Shirt
