    constructor(props) {
        super(props);
     
        this.state = {
            new: "",
            dem:1
        };
      }
      up = () => {
            var d=this.state.dem + 1;
            this.setState({
                dem:d
            });
    }
        down = () => {
            var d;
            if (d>1) d=this.
            this.setState({
                dem:d
            });
    }
    render(){
        var {match}=this.props;
        console.log(match)

        return(
            <div className="product">
