import React,{Component} from 'react';
import './Admin.css'
// import axios from "axios"
import axios, { post } from 'axios';
import {
    BrowserRouter as Router,
    Route, NavLink, Switch
} from "react-router-dom";
class Admin extends Component{
    constructor(props) {
        super(props);
        this.state = {
            Name:"",
            Price:0,
            NewPrice:0,
            Link:"",
            Description:"",
            name_kodau:"",file:""
        };
      }
      onChange1=(e)=> {
        this.setState({file:e.target.files[0]})
      }
      onFormSubmit=(e)=>{
        e.preventDefault() // Stop form submit
        this.fileUpload(this.state.file).then((response)=>{
          console.log(response.data);
        })
      }
    
    
      fileUpload=(file)=>{
        const url = 'http://example.com/file-upload';
        const formData = new FormData();
        formData.append('file',file)
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        console.log(form)
        return  post('/addproduct', formData,config)
      }
    
      add=(e)=>{
        e.preventDefault();
        var Name=this.state.Name;
        var Price=this.state.Price;
        var NewPrice=this.state.NewPrice;
        var Link=this.state.Link;
        var Description=this.state.Description;
        var name_kodau=this.state.name_kodau;
        const formData = new FormData();
        console.log(this.state.file)
        formData.append('file',this.state.file)
        console.log(formData,'111',formData) 
    //     axios.post('/addproduct',{Name:Name,Price:Price,NewPrice:NewPrice,Link:Link,Description:Description,name_kodau:name_kodau,file:this.state.file},this.state.file)
    //     .then(res=>{
    //         console.log(res.data);
    //     })
    //     .catch(err => console.log(err))
    //    } 
       axios.post('/addproduct',this.state.file)
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
       } 
       onChange =(e)=>{
        // console.log(e.target)
        var name=e.target.name;
        var value=e.target.value;
        this.setState({
          [name]:value
        })
      }
    render(){
      return(
        <Router>
            <div className="nav1">
                <ul>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                </ul>
                
            </div>
            <div className="navright">
                <form onSubmit={this.onFormSubmit}>
                    <div> 
                        <p> tên sản phẩm</p>
                        <input value={this.state.Name} name="Name" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> giá sản phẩm</p>
                        <input type="number" min="0" name="Price" value={this.state.Price} onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> giá mới</p>
                        <input type="number" min="0" name="NewPrice" value={this.state.NewPrice} onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> link </p>
                        <input value={this.state.Link} name="Link" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> mô tả sản phẩm </p>
                        <input value={this.state.Description} name="Description" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> tên sản phẩm không dấu </p>
                        <input value={this.state.name_kodau} name="name_kodau" onChange={this.onChange}></input>
                    </div>
                    <input type="file" name="file" onChange={this.onChange1}></input>
                    <button type="submit" >ADD</button>
                </form>
            </div>
        </Router>
      )
    }
}
export default Admin;
