import React,{Component} from 'react';
import Frame from '../Frame/Frame';
import './Search.css'
import { Form } from 'react-bootstrap';
import axios from "axios"
class Search extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data:[],
            name:''
        };
      }
      componentDidMount() {
        axios.get('/search1')
        .then(res=>{
          console.log(res.data);
          this.setState({
            data:res.data
        });
        })
        axios.get('/search2')
        .then(res=>{
          console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    Show = (menus) => {
        var result = null;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                var link=menu.Link+"_to1"
                return(
                    <Frame className="flex-left" anh={link} ten={menu.Name} giamoi={menu.NewPrice} giacu={menu.Price} to={menu.Link}>  </Frame>
                )
            })
        }
        return result;
    }
    render(){
        return(
            <div className="shirt">
                <pre className="color-darkgray">HOME  </pre>
                <p class="size-20"><b>Kết quả tìm kiếm cho {this.s} </b></p>
                <div className=" flex-container ">
                    {/* <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000"  ></Frame> */}
                    {this.Show(this.state.data)}
                </div>
                
            </div>
        
        )
}}
export default Search
