import React,{Component} from 'react';
import './Admin.css'
import axios from "axios"
import {
    BrowserRouter as Router,
    Route, NavLink, Switch
} from "react-router-dom";
class Admin extends Component{
    constructor(props) {
        super(props);
        this.state = {
            name:"",
            Price:0,
            NewPrice:0,
            link:"",
            Description:"",
            name_kodau:""
        };
      }
      add=()=>{
        var data=this.state.data;
        var id=data.id;
        var soluong=this.state.soluong;
        var size=this.state.size;
        var name=data.Name;
        var gia=data.NewPrice;
        var link=data.Link;
        console.log(data);
        console.log(id,name,gia);
        axios.post('/addproduct',{id:id,soluong:soluong,size:size,name:name,gia:gia,link:link})
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
       } 
    render(){
      return(
        <Router>
            <div className="nav1">
                <ul>
                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
