                    <li><i class="fas fa-plus"></i>Thêm sản phẩm</li>
                </ul>
                
            </div>
            <div className="navright">
                <div> 
                    <p> tên sản phẩm</p>
                    <input></input>
                </div>
                <div>
                    <p> giá sản phẩm</p>
                    <input></input>
                </div>
                <div>
                    <p> giá mới</p>
                    <input></input>
                </div>
                <div>
                    <p> link </p>
                    <input></input>
                </div>
                <div>
                    <p> mô ả sản phẩm </p>
                    <input></input>
                </div>
                <div>
                    <p> tên sản phẩm không dấu </p>
                    <input></input>
                </div>
                <button>ADD</button>
            </div>
        </Router>
      )
    }
}
