import React,{Component} from 'react';
import './NewTop.css'
class NewTop extends Component{
    constructor(props) {
        super(props);
     
        this.state = {
            new: ["col ",'col ','col ','col ','col none','col none'],
            dem:0
        };
      }
     Next = () => {
         if(this.state.dem<7){
        var newAaray = ["col none",'col none','col none','col none','col none','col none'];
        var d=this.state.dem+1;
        newAaray[d]="col";
        newAaray[d+1]="col";
        newAaray[d+2]="col";
        newAaray[d+3]="col";
        console.log(d);
        console.log(newAaray);
        this.setState({
            dem:d,
            new: newAaray
        });
        console.log(this.state.dem);
        console.log(this.state.new)
    }}
    Back = () => {
        if (this.state.dem>0){
        var newAaray = ["col none",'col none','col none','col none','col none','col none'];
        var d=this.state.dem-1;
        newAaray[d]="col";
        newAaray[d+1]="col";
        newAaray[d+2]="col";
        newAaray[d-1]="col";
        this.setState({
            dem:this.state.dem-1,
            new: newAaray
        });
        console.log(this.state.dem);
        console.log(this.state.new)
    }}
    render(){
        return(
            <div className="new-top">
                <p>Sản Phẩm Mới</p>
                <div className="row">
                    <div className={this.state.new[0]}>
                        <img  src="http://localhost:4000/anh/anh1"></img>
                    </div>
                    <div className={this.state.new[1]}>
                        <img src="http://localhost:4000/anh/anh2"></img>
                    </div>
                    <div className={this.state.new[2]}>
                        <img src="http://localhost:4000/anh/anh3"></img>
                    </div>
                    <div className={this.state.new[3]}>
                        <img src="http://localhost:4000/anh/anh4"></img>
                    </div>
                    <div className={this.state.new[4]}>
                        <img src="http://localhost:4000/anh/anh5"></img>
                    </div>
                    <div className={this.state.new[5]}>
                        <img src="http://localhost:4000/anh/anh6"></img>
                    </div>
                </div>
                <i class="fas fa-arrow-left icon" onClick={this.Back} ></i>
                <i class="fas fa-arrow-right icon" onClick={this.Next}></i>
            </div>
        )
}}
export default NewTop
