import React,{Component} from 'react';
import './Cart.css'
import axios from "axios"
class Cart extends Component{
    constructor (props) {
        super(props)
        this.state = {
          order:[]
        }
     }
    componentDidMount() {
        axios.get('/order')
        .then(res=>{
        //   console.log(res.data);
          this.setState({
            order:res.data
        });
        })
    }
    Show = (menus) => {
        var result = null;
        var ad= null;
        if (menus.length > 0) {
            result = menus.map((menu, index) => {
                if (menu.completed === true) {
                    ad="completed";
                    // {this.count1}                    
                } else {
                    ad="uncompleted";
                }
                return (
                    <span className="content-row" >
                        <span className="content-col col-id" >{menu.id}</span>
                        <span className="content-col col-info-user">{menu.title}</span>
                        <span className="content-col program-info">{ad}</span>
                    </span>
                )
            })
        }
        return result;
    } 
  render(){
      return(
        <div className="cart">
            <p><b>GIỎ HÀNG</b></p>
            <div className="tb">
                <div className="tb1">
                    <div className="row tb1top">
                        <div className="col-7"><p>TÊN SẢN PHẨM</p></div>
                        <div className="col-5 row">
                            <div className="col"><p>GIÁ TIỀN</p></div>
                            <div className="col"><p>SỐ LƯỢNG</p></div>
                            <div className="col"><p>CỘNG</p></div>
                        </div>
                    </div>
                    <div className="row tb1top" >
                        <div className="col-7"><p>quan</p></div>
                        <div className="col-5 row">
                            <div className="col"><p>GIÁ TIỀN</p></div>
                            <div className="col"><p>SỐ LƯỢNG</p></div>
                            <div className="col"><p>CỘNG</p></div>
                        </div>
                    </div>
                    <div className="row tb1top" >
                        <div className="col-7 clear">
                            <button className="cart-button"><b>XÓA GIỎ HÀNG</b></button>
                        </div>
                        <div className="col-5 row">
                            <div className="col-7 c">
                                <button className="cart-button"><b>TIẾP TỤC MUA HÀNG</b></button>
                            </div>
                            <div className="col-5 c">
                                <button className="cart-button"><b>CẬP NHẬT</b></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-7"></div>
                <div className="col-5 tb2 ">
                    <div className="row">
                        <div className="col-8">
                            Cộng
                        </div>
                        <div className="col-4">
                            0
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                          <p>Shipping &#38; Handling (Phí vận chuyển)</p>
                        </div>
                        <div className="col-4">
                            0
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                            <b>Tổng</b>
                        </div>
                        <div className="col-4">
                            <b>0</b>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      )
  }
}
export default Cart;
