import React,{Component} from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Body-bottom.css';
import {
  BrowserRouter as Router,
   Link
} from "react-router-dom";
import axios from "axios"
class Bodybottom extends Component {
  constructor (props) {
    super(props)
    this.state = {
      chua:"abc",
      roi:"abc none",
      hits:'aaaaa'
    }
 }
 componentDidMount() {
  fetch('http://localhost:4000/test' )
    .then(response => response.json())
    .then(data => this.setState({ hits: data }));
    // console.log(this.state.hits);
}   
 handleRequert(){
   var {username,password }=this.refs;
  axios.post('/login1',{username:username.value,password:password.value})
   .then(res=>{console.log(res.data)
  })
   .catch(err => console.log(err))
 }
 
  render(){
    const { hits } = this.state;
        console.log(hits);
  return (
    <div>
      
      {/* <input placeholder="Username" type="text" id="username" name="username" className="borderinput" ></input>
      <input placeholder="Password" type="password" id="password" name="password" className="borderinput"></input>
      <button onClick={this.handleRequert.bind(this )}>rea</button> */}
      <div className=" header-top " >
        <div className={this.state.chua}>
        <span className="aa"><i className="fas fa-user space-left" > Đăng Ký {hits} </i></span>
          <span className="">
            <i className="fas fa-sign-in-alt space-right dropdown-toggle aa" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false"> Đăng Nhập </i>
            <div className="dropdown-menu login">
              <div ><input placeholder="Username"  type="text" id="username" ref="username" className="borderinput" ></input></div>
              <div><input placeholder="Password"  type="password" id="password" ref="password" className="borderinput"></input></div>
              <button onClick={this.handleRequert.bind(this)}><b>ĐĂNG NHẬP</b></button>
              <p> ĐĂNG NHẬP VỚI TƯ CÁCH ADMIN</p>
          </div>  
          </span>
          
        </div>
      </div>
      <div className=" header-top " >
        <div className={this.state.roi}>
          <span className="aa"><i className="fas fa-user space-left" > Đăng Ký</i></span>
          <span className="aa">
            <i className="fas fa-sign-in-alt space-right  " > Đăng Nhập </i>
            
          </span>
          
        </div>
      </div>
      <div className="body-bottom">
        <div className="row ">
          <div className="col-6">
            <a href="/"><img src="http://localhost:4000/logo"  className="logo" alt="a"></img></a>
          </div>
          <div className="col-3 center formsearch border-bottom1">
            <div className="row" >
              <div className="col-3 cu dropdown-toggle" data-toggle="dropdown"  >Tất Cả</div>
              <div className="dropdown-menu bordersearch">
                <a href="/Shirt"><p>Shirt</p></a>
                <a href="#"><p>quần</p></a>
              </div>
             <div className="col-7">
              <input type="text" class="form-control-plaintext" id="search" placeholder="search"></input>
             </div>
             <div className="col-2 cu">
              <button type="button" class="btn btn-outline button-search"><i class="fas fa-search"></i></button>
             </div>
            </div>
          </div>
          <div className="col-3 left shop">
            <div className="row">
              <div className="col shop-div">
                <p><i class="fas fa-shopping-cart size50 iconshop"></i></p>
                <p className="shop-value">a</p>
              </div>
              <div className="col shop-div">
                <p className="shop-div-p">Giỏ Hàng</p>
                <p> 0 đ</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}}

export default Bodybottom;
