import React,{Component} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form } from 'react-bootstrap';
import {
  BrowserRouter as Router,
  Route, Link
} from "react-router-dom";
import axios from "axios"
import Createuser from './Createuser';
class Login extends Component {
  constructor(props) {
    super(props);
 
    this.state = {
        data:{},
        new: "",
        soluong:1,
        id:1,
        class1:["col a",'col a1','col a1'],
        size:"S",
        link:'login1'
    };
  }
  handleRequert(){
    var {username,password }=this.refs;
   axios.post('/login1',{username:username.value,password:password.value})
    .then(res=>{
      //  if (res.data="that bai") {this.setState({a:"ac"})} else  this.setState({a:""}) ;
    //  console.log(res.data);
     console.log(this.state.a);
   })
    // .catch(err => console.log(err))
  }
  render(){
  return (
    <div>
      <div class="page-title">
        <h1>Khách hàng đã đăng ký</h1>
        <div class="field note">If you have an account, sign in with your email address.</div>
      </div>
      <ul class="messages"><li class="error-msg"><ul><li><span>Login and password are required.</span></li></ul></li></ul>
      <div className="dropdown-menu login">
        <form  action="/"> 
          <div ><input placeholder="Username"  type="text" id="username" ref="username" className="borderinput" ></input></div>
          <div><input placeholder="Password"  type="password" id="password" ref="password" className="borderinput"></input></div>
          <a ><button type="submit" onClick={this.handleRequert.bind(this) } ><b>ĐĂNG NHẬP</b></button></a>
        </form>
      </div>
    </div>
  );
}}

export default Login;
