import React,{Component} from 'react';
import './Products.css';
// import './Shirt.css'
class Products extends Component{
    constructor(props) {
        super(props);
     
        this.state = {
            new: "",
            dem:1
        };
      }
      up = () => {
            var d=this.state.dem + 1;
            this.setState({
                dem:d
            });
    }
        down = () => {
            var d=this.state.dem;
            if (d>1) d=d-1;
            this.setState({
                dem:d
            });
    }
    render(){
        var {match}=this.props;
        console.log(match)

        return(
            <div className="product">
                <pre className="color-darkgray">HOME  <i className="fas fa-arrow-right color-black"></i> <b className="color-black"> {this.props.ten}</b></pre>
                <div className="boder-product">
                    <div className=" row ">
                        <div className=" col-6 ">
                            <a href="http://localhost:4000/anh/12" className="MagicZoom abc24" id="trainer"  data-options="zoomPosition: inner"><img src="http://localhost:4000/anh/12be"></img></a>
                            <div className="abc2" >
                                <a data-zoom-id="trainer" href="http://localhost:4000/anh/12be" data-image="http://localhost:4000/anh/12be"><img src="http://localhost:4000/anh/12tiny"></img></a>
                                <a data-zoom-id="trainer" href="http://localhost:4000/anh/12be" data-image="http://localhost:4000/anh/12be"><img src="http://localhost:4000/anh/12tiny"></img></a>
                                <a data-zoom-id="trainer" href="http://localhost:4000/anh/12be" data-image="http://localhost:4000/anh/12be"><img src="http://localhost:4000/anh/12tiny"></img></a>
                                <a data-zoom-id="trainer" href="http://localhost:4000/anh/12be" data-image="http://localhost:4000/anh/12be"><img src="http://localhost:4000/anh/12tiny"></img></a>
                                <a data-zoom-id="trainer" href="http://localhost:4000/anh/12be" data-image="http://localhost:4000/anh/12be"><img src="http://localhost:4000/anh/12tiny"></img></a>
                            </div>
                        </div>
                        <div className="col"></div>
                        <div className="col-5">
                            <div className="sizeM">
                                <p>SIZE</p>
                                <div className="row" >
                                    <div className='col-4 row'>
                                        <div className="col">
                                            <button className="size tooltip1">S<span className="tooltiptext1">S</span></button>
                                        </div>
                                        <div className="col">
                                            <button className="size tooltip1">M<span className="tooltiptext1">M</span></button>
                                        </div>
                                        <div className="col">
                                            <button className="size tooltip1">L<span className="tooltiptext1">L</span></button>
                                        </div>
                                    </div>
                                    <div className='col-4'></div>
                                    <div className='col-4'></div>
                                </div>
                            </div>
                            <p className="color-red">* Thông tin bắt buộc</p>
                            <div className="row">
                                <div className="col-5 row">
                                    <button className="col-3 a12 tooltip1" onClick={this.down}>
                                        <b>-</b>
                                        <span className="tooltiptext1">decrease</span>   
                                    </button>
                                    <div className="col-6 a12">{this.state.dem}</div>
                                    <button className="col-3 a12 tooltip1" onClick={this.up}>
                                        <b>+</b>
                                        <span className="tooltiptext1">increase</span>
                                    </button>
                                </div>
                                <div className="col-7"></div>
                            </div>
                            <button className="add">THÊM VÀO GIỎ HÀNG</button>
                            <p>MÔ TẢ SẢN PHẨM</p>
                        </div>
                    </div>
                </div>
                <div className="product-buttom">
                    <div className="aaa">
                        <div className="aaa1"><p>THÔNG TIN SẢN PHẨM</p></div>
                    </div>
                    <div className="aaa2">aa</div>
                </div>
            </div>
        )
}}
export default Products
