import React,{Component} from 'react';
// import './Admin.css'
import Highcharts from 'highcharts';
import axios from 'axios';
import Linecharts from './Linecharts';
import HighchartsReact from 'highcharts-react-official';
class HomeAdmin extends Component{
  constructor(props) {
    super(props); 
    var today=new Date();
    this.state = {
      d:1,
      chartOptions:{
        chart: {
            type: 'line'
        },
        title: {
            text: 'Doanh thu theo thaﾌ］g'
        },
        subtitle: {
            text: ''
        },
        xAxis: {
             categories: ['1-10', '11-20', '20-end']
        },
        yAxis: {
            title: {
                text: 'Doanh Thu'
            }
        },
        plotOptions: {
        line: {
            dataLabels: {
            enabled: true
            },
            enableMouseTracking: false
        }
        },
        series: [ {
            name: 'London',
            data: [1500000,1000000,2000000]
          },{
              name:'thang 1',
              data:[3522000,58000,3000000]
          }]
    }
    }
    
  }
  componentDidMount() { 
      
      axios.get('/revenue12')
      .then(res=>{
        var series=this.state.chartOptions.series;
        var d=this.state.d;
        var today=new Date;
        series[d+1]={
          name: 'Thaﾌ］g '+ (today.getMonth()+1),
          data: res.data
        }
        this.setState({
            chartOptions: {
                series: series
              },
            d:d+1
            
      });
    })
  }
  
   a=()=>{
