            <h1 >Admin</h1>
            {/* <Linecharts data={this.state.series}></Linecharts> */}
            <HighchartsReact
                highcharts={Highcharts}
                options={this.state.chartOptions}
            />
            <button onClick={this.a} >aaaaa</button>
        </div>
      )
    }
}
export default HomeAdmin;
