import React,{Component} from 'react';
import './Cart.css'
import axios from "axios"
class Cart extends Component{
    constructor (props) {
        super(props)
        this.state = {
          order:[],
          name:'',
          tong:1,
          value:2
        }
     }
    componentDidMount() {
        axios.get('/order')
        .then(res=>{
          console.log(res.data);
          this.setState({
            order:res.data
        });
        })
        axios.get('/test')
        .then(res=>{
          console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    change =(menus)=>{
        
    }
    
    Show = (menus,name) => {
        var result = null;
        var tong=0;
        var i=0;
        
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                console.log(menus)
                if (menu.username==name){
                    tong=tong+menu.gia*menu.soluong;
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><a href={menu.link}><img src={"http://localhost:4000/anh/"+menu.link+'_nho1'}></img></a></div>
                                <div className="col-9">
                                    <a href={menu.link}><h5>{menu.name}</h5></a>
                                    <h6>Size:  {menu.size}</h6>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>{menu.gia}</p></div>
                                <div className="col">
                                    <input type="number" value={menu.soluong} min="1"  onChange={this.change(menus,i)}></input>
                                </div>
                                <div className="col"><p>{menu.gia*menu.soluong}</p></div>
                            </div>
                        </div>
                    )
                }
                i=i+1;
            })
            if (tong!=this.state.tong)
            this.setState({
                tong:tong
            });
        }
        return result;
    } 

  render(){
      
      var data=this.state.order;
      console.log(data)
      return(
        <div className="cart">
            <p><b>GIỎ HÀNG</b></p>
            <div className="tb">
                <div className="tb1">
                    <div className="row tb1top">
                        <div className="col-7 clear"><p className="">TÊN SẢN PHẨM</p></div>
                        <div className="col-5 row">
                            <div className="col"><p>GIÁ TIỀN</p></div>
                            <div className="col"><p>SỐ LƯỢNG</p></div>
                            <div className="col"><p>CỘNG</p></div>
                        </div>
                    </div>
                    {this.Show(data,this.state.name)}
                    <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><img src="http://localhost:4000/anh/12tiny"></img></div>
                                <div className="col-9">
                                    <h5>quan</h5>
                                    <h6>Size:</h6>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>a</p></div>
                                {/* <input type="text" value="2"></input> */}
                                <div className="col">
                                    <input type="number" value={this.state.value} min="1"  onChange={this.change}></input>
                                </div>
                                <div className="col"><p>a</p></div>
                            </div>
                        </div>
                    <div className="row tb1top" >
                        <div className="col-7 clear">
                            <button className="cart-button"><b>XÓA GIỎ HÀNG</b></button>
                        </div>
                        <div className="col-5 row">
                            <div className="col-7 c">
                                <button className="cart-button"><b>TIẾP TỤC MUA HÀNG</b></button>
                            </div>
                            <div className="col-5 c">
                                <button className="cart-button"><b>CẬP NHẬT</b></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-7"></div>
                <div className="col-5 tb2 ">
                    <div className="row">
                        <div className="col-8">
                            Cộng
                        </div>
                        <div className="col-4">
                            {this.state.tong}
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                          <p>Shipping &#38; Handling (Phí vận chuyển)</p>
                        </div>
                        <div className="col-4">
                            30000
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                            <b>Tổng</b>
                        </div>
                        <div className="col-4">
                            <b>{this.state.tong+30000}</b>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      )
  }
}
export default Cart;
