import React,{Component} from 'react';
import './NewTop.css'
import Axios from 'axios';
class NewTop extends Component{
    constructor(props) {
        super(props);
     
        this.state = {
            new: ["col ",'col ','col ','col ','col none','col none'],
            dem:0,data:[]
        };
      }
      componentDidMount(){
          Axios.get("/newproduct").
          then(res=>{
              this.setState({
                  data:res.data
              })
          })
      }
     Next = () => {
         console.log(this.state.dem)
         if(this.state.dem<2){
        var newAaray = Array(6).fill("col none")
        var d=this.state.dem+1;
        newAaray[d]="col";
        newAaray[d+1]="col";
        newAaray[d+2]="col";
        newAaray[d+3]="col";
        console.log(d);
        console.log(newAaray);
        this.setState({
            dem:d,
            new: newAaray
        });
        console.log(this.state.dem);
        console.log(this.state.new)
    }}
    Back = () => {
        console.log(this.state.dem)
        if (this.state.dem>0){
            var newAaray = Array(6).fill("col none")
            var d=this.state.dem-1;
            newAaray[d]="col";
            newAaray[d+1]="col";
            newAaray[d+2]="col";
            newAaray[d+3]="col";
            this.setState({
                dem:d,
                new: newAaray
            });
            console.log(this.state.dem);
            console.log(this.state.new)
    }}
    showdata=()=>{
        this.state.data.map((data,index)=>{
            var link="http://localhost:4000/anh/"+data.Link;
            return(
                <div className={this.state.new[index]}>
                    <div class="container1">
                        <a href={da}>
                        <img className="img1" src="http://localhost:4000/anh/dam1_1" alt="Avatar"></img>
                        <div class="overlay">
                        <img className="img1" src="http://localhost:4000/anh/dam1_5"></img>
                        </div></a>
                    </div>
                        
                    </div>
            )
        })
    }
    render(){
        return(
            <div className="new-top">
                <p>Sản Phẩm Mới</p>
                <div className="row slideshow-container">
                    <div className={this.state.new[0]}>
                    <div class="container1">
                        <a href="dam1">
                        <img className="img1" src="http://localhost:4000/anh/dam1_1" alt="Avatar"></img>
                        <div class="overlay">
                        <img className="img1" src="http://localhost:4000/anh/dam1_5"></img>
                        </div></a>
                    </div>
                        
                    </div>
                    <div className={this.state.new[1]}>
                        <div class="container1">
                            <a href="thun2">
                                <img className="img1" src="http://localhost:4000/anh/thun2_1" alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src="http://localhost:4000/anh/thun2_5"></img>
                                </div>
                            </a>
                        </div>
                    </div>
                    <div className={this.state.new[2]}>
                        <div class="container1">
                            <a href="somi3">
                                <img className="img1" src="http://localhost:4000/anh/somi3_1" alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src="http://localhost:4000/anh/somi3_5"></img>
                                </div>
                                
                            </a>
                        </div>
                    </div>
                    <div className={this.state.new[3]}>
                        <div class="container1">
                            <a href="jum4">
                                <img className="img1" src="http://localhost:4000/anh/jum4_1" alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src="http://localhost:4000/anh/jum4_5"></img>
                                </div>
                            </a>
                        </div>
                    </div>
                    <div className={this.state.new[4]}>
                        <div class="container1">
                            <a href="yem5">
                                <img className="img1" src="http://localhost:4000/anh/yem5_1" alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src="http://localhost:4000/anh/yem5_5"></img>
                                </div>
                            </a>
                        </div>
                    </div>
                    <div className={this.state.new[5]}>
                        <div class="container1">
                            <a href="jean4">
                                <img className="img1" src="http://localhost:4000/anh/jean4_1" alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src="http://localhost:4000/anh/jean4_5"></img>
                                </div>
                            </a>
                        </div>
                    </div>
                </div>
                <i class="fas fa-arrow-left  prev" onClick={this.Back} ></i>
                <i class="fas fa-arrow-right next" onClick={this.Next}></i>

            </div>
        )
}}
export default NewTop
