import React,{Component} from 'react';
import Frame from '../../Frame/Frame';
import './Shirt_thun.css'
import { Form } from 'react-bootstrap';
import axios from "axios"
class Shirt_thun extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data:[]
        };
      }
      componentDidMount() {
        axios.get('/shirt1_thun')
        .then(res=>{
          console.log(res.data);
          this.setState({
            data:res.data
        });
        })
    }
    Show = (menus) => {
        var result = null;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                var link=menu.Link+"_to1"
                return(
                    <Frame className="flex-left" anh={link} ten={menu.Name} gia={menu.NewPrice}>  </Frame>
                )
            })
        }
        return result;
    }
    render(){
        return(
            <div className="shirt">
                <pre className="color-darkgray">HOME  <i class="fas fa-arrow-right color-black"></i> <b className="color-black"> SHIRT <i class="fas fa-arrow-right color-black"></i><b>Len</b></b></pre>
                <p class="size-20"><b>ÁO</b></p>
                <div className=" flex-container ">
                    <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000"  ></Frame>
                    {this.Show(this.state.data)}
                </div>
                
            </div>
        
        )
}}
export default Shirt_thun
