import React,{Component} from 'react';
import './Order.css'
import dayjs from "dayjs"
import toArray from 'dayjs/plugin/toArray'
import axios from "axios";
import Showorder from '../Showorder';
class Orders extends Component{
    constructor(props) {
        super(props);
        this.state = {
            date:dayjs().format("YYYY-MM-DD"),
            data: [],
            data1: [],
            name: "",
            tong: [],
            date1:[]
        };
      }
      componentDidMount() {
        axios.get("/orderadmin1").then(res => {
          this.setState({
            data: res.data.data,
            date1:res.data.time
          });
        });
      }
    onChange =(e)=>{
        // console.log(e.target)
        dayjs.extend(toArray);
        var name=e.target.name;
        var value=e.target.value;
        this.setState({
          [name]:value
        })
        var tomorow=dayjs(value).add(1, 'day');
      
        tomorow=tomorow.toArray()
        var value2=tomorow[0]+'-'+(tomorow[1]+1)+'-'+tomorow[2]
        axios.post('/orderadmin',{date:e.target.value,tomorow:value2})
        .then(res=>{
          this.setState({
            data: res.data.data,
            date1:res.data.time
          });
        })
        .catch(err => console.log(err))
      }
      render(){
      return(
        <div  className="navright">
            <h1> Đơn Hàng</h1>
            <div className="order">
                <input type="date" name="date" value={this.state.date} onChange={this.onChange} ></input>
                <Showorder data={this.state.data} date1={this.state.date1}></Showorder>
            </div>
            <div>

            </div>
        </div>
      )
    }
}
export default Orders;
