import React,{Component} from 'react';
import './Cart.css'
import axios from "axios"
class Cart extends Component{
    constructor (props) {
        super(props)
        this.state = {
          order:[],
          name:'',
          tong:1
        }
     }
    componentDidMount() {
        axios.get('/order')
        .then(res=>{
          console.log(res.data);
          this.setState({
            order:res.data
        });
        })
        axios.get('/test')
        .then(res=>{
          console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    Show = (menus,name) => {
        var result = null;
        var tong=0;
        if (menus.length > 0) {
            result = menus.map((menu, index) => {
                if (menu.username==name){
                    tong=tong+menu.gia*menu.soluong;
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row">
                                <div className="col"></div>
                                <div className="col"><p>{menu.namesp}</p></div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>{menu.gia}</p></div>
                                <div className="col"><p>{menu.soluong}</p></div>
                                <div className="col"><p>{menu.gia*menu.soluong}</p></div>
                            </div>
                        </div>
                    )
                }
            })
            if (tong!=this.state.tong)
            this.setState({
                tong:tong
            });
        }
        return result;
    } 
  render(){
      console.log(this.state)
      return(
        <div className="cart">
            <p><b>GIỎ HÀNG</b></p>
            <div className="tb">
                <div className="tb1">
                    <div className="row tb1top">
                        <div className="col-7"><p>TÊN SẢN PHẨM</p></div>
                        <div className="col-5 row">
                            <div className="col"><p>GIÁ TIỀN</p></div>
                            <div className="col"><p>SỐ LƯỢNG</p></div>
                            <div className="col"><p>CỘNG</p></div>
                        </div>
                    </div>
                    {this.Show(this.state.order,this.state.name)}
                    <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><img src="http://localhost:4000/anh/12tiny"></img></div>
                                <div className="col-9">
                                    <h5>quan</h5>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>a</p></div>
                                <div className="col"><p>a</p></div>
                                <div className="col"><p>a</p></div>
                            </div>
                        </div>
                    <div className="row tb1top" >
                        <div className="col-7 clear">
                            <button className="cart-button"><b>XÓA GIỎ HÀNG</b></button>
                        </div>
                        <div className="col-5 row">
                            <div className="col-7 c">
                                <button className="cart-button"><b>TIẾP TỤC MUA HÀNG</b></button>
                            </div>
                            <div className="col-5 c">
                                <button className="cart-button"><b>CẬP NHẬT</b></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-7"></div>
                <div className="col-5 tb2 ">
                    <div className="row">
                        <div className="col-8">
                            Cộng
                        </div>
                        <div className="col-4">
                            {this.state.tong}
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                          <p>Shipping &#38; Handling (Phí vận chuyển)</p>
                        </div>
                        <div className="col-4">
                            30.0
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                            <b>Tổng</b>
                        </div>
                        <div className="col-4">
                            <b>{this.state.tong+30}</b>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      )
  }
}
export default Cart;
