import React,{Component} from 'react';
// import Frame from '../Frame/Frame';
// import './Search.css'
// import { Form } from 'react-bootstrap';
import axios from "axios"
class Repair extends Component{
    constructor(props) {
        super(props);
        this.state = {
            data:[],
            name:''
        };
      }
      componentDidMount() {
        axios.get('/products')
        .then(res=>{
          console.log(res.data);
          this.setState({
            data:res.data
        });
        })
        axios.get('/search2')
        .then(res=>{
          console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    Show = (menus) => {
        var result = null;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                var link=menu.Link+"_to1"
                var name=this.props.to;
                var anh ='http://localhost:4000/anh/' + link;
                var anhhover='http://localhost:4000/anh/'+menu.Link+"_to3";
                return(
                    <div className="frame">
                        <a href={`/${name}`}  >
                            <div class="container1">
                                <img className="img1" src={anh} alt="Avatar"></img>
                                <div class="overlay">
                                <img className="img1" src={anhhover}></img>
                                </div>
                            </div>
                            <p className="frame-p">{menu.Name} </p>
                            <b>
                                <span className="frame-giacu"> {menu.Price+'đ'}</span>
                                <span className="frame-giamoi"> {menu.NewPrice +"đ"}</span>
                            </b>
                        </a>
                        
                    </div>
                )
            })
        }
        return result;
    }
    render(){
        console.log(this.state.data)
        return(
            <div className="navright">
                <div className="shirt">
                    {/* <pre className="color-darkgray">HOME <i class="fas fa-arrow-right color-black"></i> <b className="color-black"> Search</b>  </pre> */}
                    <p class="size-20"><b>T</b></p>
                    <div className=" flex-container ">
                        {/* <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000"  ></Frame> */}
                        {this.Show(this.state.data)}
                    </div>
                    
                </div>
            </div>
        
        )
}}
export default Repair
