import React,{Component} from 'react';
import Frame from '../Frame/Frame';
import './Shirt.css'

import axios from "axios"
class Shirt extends Component{
    constructor(props) {
        super(props);
        this.state = {
            currentPage:1,
            newsPerPage:6,
            pageNumbers:1
        };
      }
    changepage=(pageNumbers)=>{
        this.setState({
            pageNumbers:pageNumbers
        })
    }

    Show = (menus,match,pageNumbers) => {
        var result = null;
        if (menus !== undefined){
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                var link=menu.Link+"_1"
                if (index>=(pageNumbers-1)*this.state.newsPerPage && index <= pageNumbers*this.state.newsPerPage-1)
                return(
                    <Frame match={match.match} className="flex-left" anh={link} ten={menu.Name} giamoi={menu.NewPrice} giacu={menu.Price} to={menu.Link}>  </Frame>
                )
            })
        }}
        return result;
    }
    render(){
        var match=this.props;
        return(
            <div className="shirt">
                <pre className="color-darkgray">HOME  <i class="fas fa-arrow-right color-black"></i> <b className="color-black"> {this.props.Name}</b></pre>
                <div className=" flex-container ">
                    {/* <Frame className="flex-left"  anh="111" ten="Váy đầm trắng" gia="320.000" to="aaa"  ></Frame> */}
                    {this.Show(this.props.data,match,this.state.pageNumbers)}
                </div>
                <div className="shirt_page"> 
                    <button className="shirt_button_page" onClick={()=>this.changepage(1)}>1</button>
                    <button className="shirt_button_page" onClick={()=>this.changepage(2)}>2</button> 
                    <button className="shirt_button_page" onClick={()=>this.changepage(3)}>3</button>                   
                </div>
                <div className="page row">
                    <span className="col-3" >Trang 2 trên tổng số 5</span>
                    <ul className="col">
                        <a href="#1"><button className="shirt_button_page" onClick={()=>this.changepage(1)}>1</button></a>
                    </ul>
                </div>
                <nav class="pagination is-small custom-pagination" role="navigation" aria-label="pagination">
                    <span class="pagination-summary">Trang <b>3</b> trên tổng số <b>5</b></span>
                    <ul class="pagination-list">
                        <a class="pagination-link" id="1" href="/?page=1">
                            <li>1</li>
                        </a><a class="pagination-link" id="2" href="/?page=2"><li>2</li></a><a class="pagination-link is-current" id="3" href="/?page=3"><li>3</li></a><a class="pagination-link" id="4" href="/?page=4"><li>4</li></a><a class="pagination-link" id="5" href="/?page=5"><li>5</li></a></ul></nav>
            </div>
        
        )
}}
export default Shirt
