import React,{Component} from 'react';
import './NewTop.css'
class NewTop extends Component{
    constructor(props) {
        super(props);
     
        this.state = {
            new: ["col ",'col ','col ','col ','col none','col none'],
            dem:0
        };
      }
     Next = () => {
         console.log(this.state.dem)
         if(this.state.dem<2){
        var newAaray = ["col none",'col none','col none','col none','col none','col none'];
        var d=this.state.dem+1;
        newAaray[d]="col";
        newAaray[d+1]="col";
        newAaray[d+2]="col";
        newAaray[d+3]="col";
        console.log(d);
        console.log(newAaray);
        this.setState({
            dem:d,
            new: newAaray
        });
        console.log(this.state.dem);
        console.log(this.state.new)
    }}
    Back = () => {
        console.log(this.state.dem)
        if (this.state.dem>0){
            var newAaray = ["col none",'col none','col none','col none','col none','col none'];
            var d=this.state.dem-1;
            newAaray[d]="col";
            newAaray[d+1]="col";
            newAaray[d+2]="col";
            newAaray[d+3]="col";
            this.setState({
                dem:d,
                new: newAaray
            });
            console.log(this.state.dem);
            console.log(this.state.new)
    }}
    render(){
        return(
            <div className="new-top">
                <p>Sản Phẩm Mới</p>
                <div className="row slideshow-container">
                    <div className={this.state.new[0]}>
                    <div class="container1">
                        <img src="http://localhost:4000/anh/anh1" alt="Avatar"></img>
                        <div class="overlay">
                        <img src="http://localhost:4000/anh/anh1-hover"></img>
                        </div>
                    </div>
                        
                    </div>
                    <div className={this.state.new[1]}>
                        <div class="container1">
                            <img src="http://localhost:4000/anh/anh2" alt="Avatar"></img>
                            <div class="overlay">
                            <img src="http://localhost:4000/anh/anh2-hover"></img>
                            </div>
                        </div>
                    </div>
                    <div className={this.state.new[2]}>
                        <div class="container1">
                            <img src="http://localhost:4000/anh/anh3" alt="Avatar"></img>
                            <div class="overlay">
                            <img src="http://localhost:4000/anh/anh3-hover"></img>
                            </div>
                        </div>
                    </div>
                    <div className={this.state.new[3]}>
                        <div class="container1">
                            <img src="http://localhost:4000/anh/anh4" alt="Avatar"></img>
                            <div class="overlay">
                            <img src="http://localhost:4000/anh/anh4-hover"></img>
                            </div>
                        </div>
                    </div>
                    <div className={this.state.new[4]}>
                        <div class="container1">
                            <img src="http://localhost:4000/anh/anh5" alt="Avatar"></img>
                            <div class="overlay">
                            <img src="http://localhost:4000/anh/anh5-hover"></img>
                            </div>
                        </div>
                    </div>
                    <div className={this.state.new[5]}>
                        <div class="container1">
                            <img src="http://localhost:4000/anh/anh6" alt="Avatar"></img>
                            <div class="overlay">
                            <img src="http://localhost:4000/anh/anh6-hover"></img>
                            </div>
                        </div>
                    </div>
                </div>
                <i class="fas fa-arrow-left icon prev" onClick={this.Back} ></i>
                <i class="fas fa-arrow-right icon next" onClick={this.Next}></i>
                {/* <a class="prev" onClick={this.Back}>&#10094;</a>
                <a class="next" onClick={this.Next}>&#10095;</a> */}
            </div>
        )
}}
export default NewTop
