import React,{Component} from 'react';
// import './Admin.css'
import Highcharts from 'highcharts';
import axios from 'axios';
class HomeAdmin extends Component{
  constructor(props) {
    super(props); 
    var today=new Date();
    this.state = {
      series: [ {
        name: 'London',
        data: [3.9, 4.2, 5.7]
      }],
      d:0
    }
  }
  componentDidMount() { 
      this.highChartsRender(); 
      axios.get('/revenue12')
      .then(res=>{
        var series=this.state.series;
        var d=this.state.d;
        var today=new Date;
        series[d+1]={
          name: 'Thaﾌ］g '+ (today.getMonth()+1),
          data: res.data
        }
        console.log(series[1]);
        this.setState({
          series:series,
          d:d+1
      });
      console.log(res.data,this.state.d)
    })
  }
   highChartsRender() {
    Highcharts.chart('container', {
      chart: {
        type: 'line'
      },
      title: {
        text: 'Doanh thu theo thaﾌ］g'
      },
      subtitle: {
        text: ''
      },
      xAxis: {
        categories: ['1-10', '11-20', '20-end']
      },
      yAxis: {
        title: {
          text: 'Doanh Thu'
        }
      },
      plotOptions: {
        line: {
          dataLabels: {
            enabled: true
          },
          enableMouseTracking: false
        }
      },
      series: this.state.series
    });
   }
   a=()=>{
    var dem=this.state.dem;
    dem[this.state.d+1]={a:'b'}
    this.setState({
      dem:dem
    })
    
    console.log(this.state.dem)
   }
    render(){
      return(
        <div className="navright">
            <h1 >Admin</h1>
            <div id="container">
  	        </div>
            <button onClick={this.a} >aaaaa</button>
        </div>
      )
    }
}
export default HomeAdmin;
