import React,{Component} from 'react';
// import './Addproduct.css'
// import axios from "axios"
import axios, { post } from 'axios';

class Repairproduct extends Component{
    constructor(props) {
        super(props);
        this.state = {
            id:0,
            Name:"",
            Price:0,
            NewPrice:0,
            Link:"",
            Description:"",
            name_kodau:"",
            file1:"",
            file2:"",
            file3:"",
            file4:"",
            file5:"",
            file6:"",
            file7:"",
            file8:"",
            file9:"",
            file10:"",
            file11:"",
            file12:"",
            file13:"",
            file14:"",
            file15:"",
            data:{}
        };
      }
      componentDidMount() {
        axios.get('/products')
        .then(res=>{
        //   console.log(res.data);
          var {match}=this.props;
        //   console.log(match.params.name);
          res.data.map((menu, index) =>{
            // console.log(match.params.name);
            if (menu.Link==match.params.name) {
                console.log(menu);
                this.setState({
                    data:menu,
                    id:menu.id,
                    Name:menu.Name,
                    Price:menu.Price,
                    NewPrice:menu.NewPrice,
                    Link:menu.Link,
                    Description:menu.Description,
                    name_kodau:menu.name_kodau,
                    file1:"dam1_to1.jpg"    
                });
                return;
            }
        })
        })
      }
      onChange1=(e)=> {
        var name=e.target.name;
        this.setState({[name]:e.target.files[0]})
      }
      add=(e)=>{
        e.preventDefault();
        var id=this.state.id;
        var Name=this.state.Name;
        var Price=this.state.Price;
        var NewPrice=this.state.NewPrice;
        var Link=this.state.Link;
        var Description=this.state.Description;
        var name_kodau=this.state.name_kodau;
        const formData = new FormData();
        // console.log(this.state.file)
        formData.append('file',this.state.file1)
        formData.append('file',this.state.file2)
        formData.append('file',this.state.file3)
        formData.append('file',this.state.file4)
        formData.append('file',this.state.file5)
        formData.append('file',this.state.file6)
        formData.append('file',this.state.file7)
        formData.append('file',this.state.file8)
        formData.append('file',this.state.file9)
        formData.append('file',this.state.file10)
        formData.append('file',this.state.file11)
        formData.append('file',this.state.file12)
        formData.append('file',this.state.file13)
        formData.append('file',this.state.file14)
        formData.append('file',this.state.file15)
        const config = {
            headers: {
                authorization:'hoanganh'
            }
        }
        axios.post('/repairproduct',{id:id,Name:Name,Price:Price,NewPrice:NewPrice,Link:Link,Description:Description,name_kodau:name_kodau})
        .then(res=>{
            console.log(res.data);
        })
        .catch(err => console.log(err))
        
    
    //    axios.post('/repairimg1',formData,config,{Name:Name,Price:Price,NewPrice:NewPrice,Link:Link,Description:Description,name_kodau:name_kodau})
    //     .then(res=>{
    //         console.log(res.data);
    //     })
    //     .catch(err => console.log(err))
       } 
       onChange =(e)=>{
        // console.log(e.target)
        var name=e.target.name;
        var value=e.target.value;
        this.setState({
          [name]:value
        })
      }
    render(){
        var {match}=this.props;
        console.log(match);
        console.log(this.state.data);
      return(
            <div className="navright">
                <form onSubmit={this.add} className="formadd flex-container" autocomplete="off">
                    <div> 
                        <p> tên sản phẩm</p>
                        <input value={this.state.Name} name="Name" onChange={this.onChange} ></input>
                    </div>
                    <div>
                        <p> giá sản phẩm</p>
                        <input type="number" min="0" name="Price" value={this.state.Price} onChange={this.onChange} ></input>
                    </div>
                    <div>
                        <p> giá mới</p>
                        <input type="number" min="0" name="NewPrice" value={this.state.NewPrice} onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> link </p>
                        <input value={this.state.Link} name="Link" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> mô tả sản phẩm </p>
                        <input value={this.state.Description} name="Description" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p> tên sản phẩm không dấu </p>
                        <input value={this.state.name_kodau} name="name_kodau" onChange={this.onChange}></input>
                    </div>
                    <div>
                        <p>anhto1</p>
                        <input type="file" name="file1" onChange={this.onChange1} ></input>
                    </div>
                    <div>
                        <p>anhto2</p>
                        <input type="file" name="file2" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhto3</p>
                        <input type="file" name="file3" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhto4</p>
                        <input type="file" name="file4" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhto5</p>
                        <input type="file" name="file5" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhvua1</p>
                        <input type="file" name="file6" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhvua2</p>
                        <input type="file" name="file7" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhvua3</p>
                        <input type="file" name="file8" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhvua4</p>
                        <input type="file" name="file9" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhvua5</p>
                        <input type="file" name="file10" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhnho1</p>
                        <input type="file" name="file11" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhnho2</p>
                        <input type="file" name="file12" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhnho3</p>
                        <input type="file" name="file13" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhnho4</p>
                        <input type="file" name="file14" onChange={this.onChange1}></input>
                    </div>
                    <div>
                        <p>anhnho5</p>
                        <input type="file" name="file15" onChange={this.onChange1}></input>
                    </div>
                    <div></div>
                    <div>
                        <button type="submit" className="buttonaddproduct btn-primary" >ADD</button>
                    </div>
                </form>
            </div>
      )
    }
}
export default Repairproduct;
