import React,{Component} from 'react';
import './Cart.css'
import axios from "axios"
class Cart extends Component{
    constructor (props) {
        super(props)
        this.state = {
          order:[],
          order1:[],
          name:'',
          tong:1,
          value:2,
          dem:[]
        }
     }
    componentDidMount() {
        axios.get('/order')
        .then(res=>{
        //   console.log(res.data);
          this.setState({
            order:res.data,
            order1:res.data
        });
        })
        axios.get('/test')
        .then(res=>{
        //   console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    update(){
        // var {search}=this.refs;
        axios.post('/update',{data:this.state.order})
        .then(res=>{
         console.log(res.data);
        //  this.setState({search:res.data})
       })
        .catch(err => console.log(err))
      }
    Show = (menus,name) => {
        var result = null;
        var tong=0;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                if (menu.username==name){
                    tong=tong+menu.NewPrice*menu.soluong;
                    // console.log(index);
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><a href={menu.Link}><img src={"http://localhost:4000/anh/"+menu.Link+'_nho1'}></img></a></div>
                                <div className="col-9">
                                    <a href={menu.Link}><h5>{menu.Name}</h5></a>
                                    <h6>Size:  {menu.size}</h6>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>{menu.NewPrice}</p></div>
                                <div className="col">
                                    <input type="number" value={menu.soluong} min="1"  onChange={e=>{
                                        var a=this.state.order;
                                        a[index].soluong=e.target.value;
                                        this.setState({order:a});
                                    }}></input>
                                </div>
                                <div className="col"><p>{menu.NewPrice*menu.soluong}</p></div>
                            </div>
                        </div>
                    )
                }
                // i=i+1;
            })
            if (tong!=this.state.tong)
            this.setState({
                tong:tong
            });
        }
        return result;
    }


  render(){
    //   var data=this.props.data;
      console.log(this.state.order);
      var d=data[0] || {soluong:1};
      return(
        <div className="cart">
            <p><b>GIỎ HÀNG </b></p>
            <div className="tb">
                <div className="tb1">
                    <div className="row tb1top">
                        <div className="col-7 clear"><p className="">TÊN SẢN PHẨM</p></div>
                        <div className="col-5 row">
                            <div className="col"><p>GIÁ TIỀN</p></div>
                            <div className="col"><p>SỐ LƯỢNG</p></div>
                            <div className="col"><p>CỘNG</p></div>
                        </div>
                    </div>
                    {this.Show(this.state.order,this.state.name)}
                    <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><img src="http://localhost:4000/anh/12tiny"></img></div>
                                <div className="col-9">
                                    <h5>quan</h5>
                                    <h6>Size:</h6>
                                </div>
                            </div>
                            <div className="col-5 row">
                                <div className="col"><p>a</p></div>
                                {/* <input type="text" value="2"></input> */}
                                <div className="col">
                                    <input type="number" value={this.state.value} min="1"  onChange={this.change}></input>
                                </div>
                                <div className="col"><p>a</p></div>
                            </div>
                        </div>
                    <div className="row tb1top" >
                        <div className="col-7 clear">
                            <button className="cart-button"><b>XÓA GIỎ HÀNG</b></button>
                        </div>
                        <div className="col-5 row">
                            <div className="col-7 c">
                                <a herf="/"><button className="cart-button"><b>TIẾP TỤC MUA HÀNG</b></button></a>
                            </div>
                            <div className="col-5 c">
                                <button className="cart-button" onClick={this.update}><b>CẬP NHẬT</b></button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-7"></div>
                <div className="col-5 tb2 ">
                    <div className="row">
                        <div className="col-8">
                            Cộng
                        </div>
                        <div className="col-4">
                            {this.state.tong}
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                          <p>Shipping &#38; Handling (Phí vận chuyển)</p>
                        </div>
                        <div className="col-4">
                            30000
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                            <b>Tổng</b>
                        </div>
                        <div className="col-4">
                            <b>{this.state.tong+30000}</b>
                        </div>
                    </div>
                </div>
            </div>
        </div>
      )
  }
}
export default Cart;
