import React, { Component } from "react";
import "./History.css";
import axios from "axios";
class History extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      data1: [],
      name: "",
      tong: 0
    };
  }
  componentDidMount() {
    axios.get("/history").then(res => {
      this.setState({
        data: res.data
      });
    });
    axios.get("/history_time").then(res => {
      this.setState({
        data1: res.data
      });
    });
    axios.get("/test").then(res => {
      //   console.log(res.data);
      this.setState({
        name: res.data
      });
    });
  }
  Show = (menus,name) => {
    var result = null;
    var tong=0;
    if (menus.length > 0) {
        result = menus.map((menu, index) =>{
            if (menu.time==name ){
                tong=tong+menu.NewPrice*menu.soluong;
                // console.log(index);
                return (
                    <div className="row tb1top" >
                        <div className="col-7 row clear">
                            <div className="col-2"><a href={menu.Link}><img src={"http://localhost:4000/anh/"+menu.Link+'_nho1'}></img></a></div>
                            <div className="col-9">
                                <a href={menu.Link}><h5>{menu.Name}</h5></a>
                                <h6>Size:  {menu.size}</h6>
                            </div>
                        </div>
                        <div className="col-5 row t1t1">
                            <div className="col-4"><p>{menu.NewPrice}</p></div>
                            <div className="col-3">
                                <input type="number" value={menu.soluong} min="0"  onChange={e=>{
                                    var a=this.state.order;
                                    // var b=this.state.order1;
                                    // console.log(b[index].soluong,b[index].soluong-Number(e.target.value),Number(e.target.value))
                                    // console.log(a,b)
                                    // b[index].soluong=b[index].soluong-Number(e.target.value);
                                    a[index].soluong=Number(e.target.value);
                                    // console.log(a,b)
                                    this.setState({order:a});
                                    // console.log(this.state.order,this.state.order1)
                                    // console.log(b[index].soluong,b[index].soluong-Number(e.target.value),Number(e.target.value))
                                }}></input>
                            </div>
                            <div className="col-4"><p>{menu.NewPrice*menu.soluong}</p></div>
                            <div className="col-1" onClick={this.de.bind(this,menu.id,menu.size)} ><a href=""><i class="fas fa-times" ></i></a></div>
                        </div>
                    </div>
                )
            }
            // i=i+1;
        })
        if (tong!=this.state.tong)
        this.setState({
            tong:tong
        });
    }
    return result;
}
  Show = (data, times) => {
    var result = null;
    console.log(data, times);
    console.log(times.length);
    // var tong=0;
    if (times.length > 0) {
      // for (var i=0;i<=times.length-1;i++){
      result = times.map((time, index) => {
          console.log(data)
        return (
          <div className="tb">
            <div>NGÀY VÀ GIỜ: {time.time} </div>
            <div className="tb1">
              <div className="row tb1top">
                <div className="col-7 clear">
                  <p className="">TÊN SẢN PHẨM</p>
                </div>
                <div className="col-5 row">
                  <div className="col-4">
                    <p>GIÁ TIỀN</p>
                  </div>
                  <div className="col-4">
                    <p>SỐ LƯỢNG</p>
                  </div>
                  <div className="col-4">
                    <p>CỘNG</p>
                  </div>
                </div>
              </div>
              {this.a(data,time.time)}
            </div>
            <div>TỔNG TIỀN : </div>
          </div>
        );
      });
    }
    return result;
    // if (menus.length > 0) {
    //     result = menus.map((menu, index) =>{
    //         if (menu.username==name ){
    //             tong=tong+menu.NewPrice*menu.soluong;
    //             return (
    //                 <div className="row tb1top" >
    //                     <div className="col-7 row clear">
    //                         <div className="col-2"><a href={menu.Link}><img src={"http://localhost:4000/anh/"+menu.Link+'_nho1'}></img></a></div>
    //                         <div className="col-9">
    //                             <a href={menu.Link}><h5>{menu.Name}</h5></a>
    //                             <h6>Size:  {menu.size}</h6>
    //                         </div>
    //                     </div>
    //                     <div className="col-5 row t1t1">
    //                         <div className="col-4"><p>{menu.NewPrice}</p></div>
    //                         <div className="col-3">
    //                             <input type="number" value={menu.soluong}></input>
    //                         </div>
    //                         <div className="col-4"><p>{menu.NewPrice*menu.soluong}</p></div>
    //                         <div className="col-1" onClick={this.de.bind(this,menu.id,menu.size)} ><a href=""><i class="fas fa-times" ></i></a></div>
    //                     </div>
    //                 </div>
    //             )
    //         }
    //     })
    //     if (tong!=this.state.tong)
    //     this.setState({
    //         tong:tong
    //     });
    // }
  };

  render() {
    //   var data=this.props.data;
    console.log(this.state.data, this.state.data1);
    //   var d=data[0] || {soluong:1};
    return (
      <div className="cart">
        <p>
          <b>LỊCH SỬ MUA HÀNG </b>
        </p>

        {this.Show(this.state.data, this.state.data1)}
      </div>
    );
  }
}
export default History;
