import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./Body.css";
import {
  BrowserRouter ,
  Route,
  Switch,
  Link
} from "react-router-dom";
import Home from "../Out/Home/Home";
// import Contact from '../Out/Contact/Contact';
import Search from "../Out/Search/Search";
// import Shirt from "../Out/Shirt/Shirt";
import History from "../Out/History/History";

import Products from "../Out/Products/Products";
// import Routershirt from '../Out/Shirt/Router';
import Contact from "../Out/Contact/Contact";
import About from "../Out/About/About";
import Login from "../Login";
import Admin from "../Admin/Admin";
import Cart from "../Out/Cart/Cart";
import axios from "axios";
import ShowProducts from "../Out/ShowProducts/ShowProducts";
const linkdatasingle=['len','thun','khoac','somi','sooc','jean','baggy','dam','jum','yem'];
const linkdatageneral=[['len','thun','khoac','somi'],['sooc','jean','baggy'],['dam','jum','yem']]
class Body extends Component {
  constructor(props) {
    super(props);
    this.state = {
      order: [],
      name: "",
      dem: [],
      datasingle:[],
      lengthdatasingle:0,
      datageneral:[],
      lengthdatadeneral:0
    };
  }
 
  componentDidMount() {
    axios.get("/order").then(res => {
      this.setState({
        order: res.data
      });
    });
    
    linkdatasingle.map((link,index)=>{
      axios.post("/datasingle",{name:link}).then(res => {
        // console.log(link,res.data);
        var datasingle=this.state.datasingle;
        var d=this.state.lengthdatasingle;
        datasingle[d]=res.data;
        this.setState({
          datasingle:datasingle,
          lengthdatasingle:d+1
        });
      });
    })
    linkdatageneral.map((link,index)=>{
      axios.post("/datageneral",{name:link}).then(res => {
        // console.log(link,res.data);
        var datageneral=this.state.datageneral;
        var d=this.state.lengthdatadeneral;
        datageneral[d]=res.data;
        this.setState({
          datageneral:datageneral,
          lengthdatadeneral:d+1
        });
      });
    })
  }

  
  render() {
    return (
      <div className="body">
        <div className={this.props.class}>
          <div className="subnav">
          <button className="subnavbtn">
              <Link to="/" ><b className="size25"><i className="fas fa-home"></i></b></Link>
            </button>
          </div>
          
          <div className="subnav">
            <button className="subnavbtn">
              <Link to="/shirt" ><b className="size25">Áo</b></Link>
            </button>
            <div className=" subnav-content ">
              <div className="row">
                <div className="col-6">
                  <div className="abc1">
                    <b>Áo</b>
                  </div>
                  <Link to="/shirt_len">
                    <div className="abcd">Áo len</div>
                  </Link>
                 
                  <Link to="/shirt_thun">
                    <div className="abcd">Áo thun</div>
                  </Link>
                  <Link to="/shirt_khoac">
                    <div className="abcd">Áo khoác</div>
                  </Link>
                  <Link to="/shirt_somi">
                    <div className="abcd">Áo sơ mi</div>
                  </Link>
                </div>
                <div className="col-6">
                  <img
                    className="img"
                    src="http://localhost:4000/anh/menu-ao-92w"
                  ></img>
                </div>
              </div>
            </div>
          </div>
          <div className="subnav">
            <button className="subnavbtn">
              <Link to="/trouser"><b className="size25">Quần</b></Link>
            </button>
            <div className=" subnav-content ">
              <div className="row">
                <div className="col-6">
                  <div className="abc1">
                    <b>Quần</b>
                  </div>
                  <Link to="/trouser_sooc">
                    <div className="abcd">Quần Sooc</div>
                  </Link>
                  <Link to="/trouser_jean">
                    <div className="abcd">Quần Jean</div>
                  </Link>
                  <Link to="/trouser_baggy">
                    <div className="abcd">Quần Baggy</div>
                  </Link>
                </div>
                <div className="col-6">
                  <img
                    className="img1"src="http://localhost:4000/92quan"></img>
                </div>
              </div>
            </div>
          </div>
          <div className="subnav">
            <button className="subnavbtn">
              <Link to="/vay"><b className="size25">Váy</b></Link>
            </button>
            <div className=" subnav-content ">
              <div className="row">
                <div className="col-6">
                  <div className="abc1">
                    <b>Váy</b>
                  </div>
                  <Link to="/vay_yem">
                    <div className="abcd">
                      <pre>Yếm       </pre>
                    </div>
                  </Link>
                  <Link to="/vay_jum">
                    <div className="abcd">
                      <pre>Jum      </pre>
                    </div>
                  </Link>
                  <Link to="/vay_dam">
                    <div className="abcd">Đầm</div>
                  </Link>
                </div>
                <div className="col-6">
                  <img className="img1" alt="anh" src="http://localhost:4000/92vay"></img>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/login1" render={props => <Login {...props} data="Khách hàng đã đăng ký" />} />
          <Route path="/loginadmin" render={props => <Login {...props} data="admin" />} />
          <Route path="/search" exact component={Search} />
          <Route path="/product/search" exact component={Search} />
          {/* <Route path="/admin" component={Admin} /> */}
          {/* <Route path="/search?" exact component={Search} /> */}
          <Route path="/contact" exact component={Contact} />
          <Route path="/about" exact component={About} />
          <Route path="/history" exact component={History} />
          <Route
            path="/cart"
            exact
            render={props => <Cart {...props} data={this.state.order} />}
          />
          {/* <Route path="/shirt" exact component={Shirt} /> */}
          <Route
            path="/shirt"
            exact
            render={props => <ShowProducts {...props} data={this.state.datageneral[0]}  Name="Áo" />}
          />
          <Route
            path="/shirt_len"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[0]}  Name="Áo Len" />
            }
          />
          <Route
            path="/shirt_thun"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[1]}  Name="Áo Thun" />
            }
          />
          <Route
            path="/shirt_khoac"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[2]}  Name="ÁO Khoác" />
            }
          />
          <Route
            path="/shirt_somi"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[3]}  Name="Áo Sơ Mi" />
            }
          />
          <Route
            path="/trouser"
            exact
            render={props => <ShowProducts {...props} data={this.state.datageneral[1]}  Name="Quần" />}
          />
          <Route
            path="/trouser_sooc"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[4]}  Name="Quần Sooc" />
            }
          />
          <Route
            path="/trouser_jean"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[5]}  Name="Quần Jean" />
            }
          />
          <Route
            path="/trouser_baggy"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[6]}  Name="Quần Baggy" />
            }
          />
          <Route
            path="/vay"
            exact
            render={props => <ShowProducts {...props} data={this.state.datageneral[2]}  Name="Váy" />}
          />
          <Route
            path="/vay_dam"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[7]}  Name="Váy Đầm" />
            }
          />
          <Route
            path="/Vay_jum"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[8]}  Name="Váy Jumsuit" />
            }
          />
          <Route
            path="/vay_yem"
            exact
            render={props => 
              <ShowProducts {...props} data={this.state.datasingle[9]}  Name="Váy Yếm" />
            }
          />
          <Route path="/shirt/aaa" component={Products} />
          <Route path="/product/:name" component={Products} />
          <Route path="/trouser/:name" component={Products} />
          <Route path="/vay/:name" component={Products} />
          <Route path="/search/:name" component={Products} />
        </Switch>
      </div>
    );
  }
}

export default Body;
