import React,{Component} from 'react';
import './Cart.css'
import axios from "axios"
class Cart extends Component{
    constructor (props) {
        super(props)
        this.state = {
          order:[],
          order1:this.props.data,
          name:'',
          tong:1,
          value:2,
          dem:[]
        }
     }
    componentDidMount() {
        axios.get('/order')
        .then(res=>{
        //   console.log(res.data);
          this.setState({
            order:res.data,
            order1:res.data
        });
        })
        axios.get('/test')
        .then(res=>{
        //   console.log(res.data);
          this.setState({
            name:res.data
        });
        })
    }
    de=(a,b)=>{
        console.log();
        axios.post('/delete',{id:a,size:b})
        .then(res=>{
         console.log(res.data);
        //  this.setState({search:res.data})
       })
        .catch(err => console.log(err))
    }
    update = () =>{
        // var {data}=this.props.order;
        console.log(this.state.order)
        axios.post('/update',{data:this.state.order})
        .then(res=>{
         console.log(res.data);
        //  this.setState({search:res.data})
       })
        .catch(err => console.log(err))
      }
      tt = () =>{
          var data=this.state.order,data1=this.state.order1;
          console.log("data",this.state.order,'data1:',this.state.order1);
        //   for ( var i=0;i<=data.length-1;i++)
        //     data1[i].soluong=data1[i].soluong-data[i].soluong;
        //     console.log('data:',data1);

      }
      cl = () =>{
        // var {data}=this.props.order;
        console.log(this.state.order)
        axios.post('/clear',{data:this.state.order})
        .then(res=>{
         console.log(res.data);
        //  this.setState({search:res.data})
       })
        .catch(err => console.log(err))
      }
    //   a{}
    Show = (menus,name) => {
        var result = null;
        var tong=0;
        if (menus.length > 0) {
            result = menus.map((menu, index) =>{
                if (menu.username==name && menu.soluong ){
                    tong=tong+menu.NewPrice*menu.soluong;
                    // console.log(index);
                    return (
                        <div className="row tb1top" >
                            <div className="col-7 row clear">
                                <div className="col-2"><a href={menu.Link}><img src={"http://localhost:4000/anh/"+menu.Link+'_nho1'}></img></a></div>
                                <div className="col-9">
                                    <a href={menu.Link}><h5>{menu.Name}</h5></a>
                                    <h6>Size:  {menu.size}</h6>
                                </div>
                            </div>
                            <div className="col-5 row t1t1">
                                <div className="col-4"><p>{menu.NewPrice}</p></div>
                                <div className="col-3">
                                    <input type="number" value={menu.soluong} min="0"  onChange={e=>{
                                        var a=this.state.order;
                                        var b=this.state.order1;
                                        b[index].soluong=b[index].soluong-Number(e.target.value);
                                        a[index].soluong=e.target.value;
                                        this.setState({order:a,order1:b});
                                        console.log(this.state.order,this.state.order1)
                                        console.log(b)
                                    }}></input>
                                </div>
                                <div className="col-4"><p>{menu.NewPrice*menu.soluong}</p></div>
                                <div className="col-1" onClick={this.de.bind(this,menu.id,menu.size)} ><a href=""><i class="fas fa-times" ></i></a></div>
                            </div>
                        </div>
                    )
                }
                // i=i+1;
            })
            if (tong!=this.state.tong)
            this.setState({
                tong:tong
            });
        }
        return result;
    }


  render(){
    //   var data=this.props.data;
      console.log(this.state.order);
    //   var d=data[0] || {soluong:1};
      return(
        <div className="cart">
            <p><b>GIỎ HÀNG </b></p>
            <div className="tb">
                <div className="tb1">
                    <div className="row tb1top">
                        <div className="col-7 clear"><p className="">TÊN SẢN PHẨM</p></div>
                        <div className="col-5 row">
                            <div className="col-4"><p>GIÁ TIỀN</p></div>
                            <div className="col-3"><p>SỐ LƯỢNG</p></div>
                            <div className="col-4"><p>CỘNG</p></div>
                            <div className="col-1"></div>
                        </div>
                    </div>
                    {this.Show(this.state.order,this.state.name)}
                    
                    <div className="row tb1top" >
                        <div className="col-7 clear">
                            <a href=""><button className="cart-button" onClick={this.cl} ><b>XÓA GIỎ HÀNG</b></button></a>
                        </div>
                        <div className="col-5 row">
                            <div className="col-7 c">
                                <a href="/"><button className="cart-button"><b>TIẾP TỤC MUA HÀNG</b></button></a>
                            </div>
                            <div className="col-5 c">
                                <a href=""><button className="cart-button" onClick={this.update}><b>CẬP NHẬT</b></button></a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <div className="col-7"></div>
                <div className="col-5 tb2 ">
                    <div className="row">
                        <div className="col-8">
                            Cộng
                        </div>
                        <div className="col-4">
                            {this.state.tong}
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                          <p>Shipping &#38; Handling (Phí vận chuyển)</p>
                        </div>
                        <div className="col-4">
                            30000
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-8">
                            <b>Tổng</b>
                        </div>
                        <div className="col-4">
                            <b>{this.state.tong+30000}</b>
                        </div>
                    </div>
                    <a ><button className="ca-button" onClick={this.tt} ><b>THANH TOÁN</b></button></a>
                </div>
            </div>
        </div>
      )
  }
}
export default Cart;
